        // 遍历拿到所有的类
        for (UInt64 addr = classlist->offset; addr offset + classlist->size; addr += sizeof(const char **)) {
            uint64_t baseArrr = mach_header + addr;

            //获取类对象指针
            uint64_t object_class_addr = *(uint64_t *)(baseArrr);
            //
            uint64_t object_class_isa = *(uint64_t *)(object_class_addr);
            //获取元类对象
            uint64_t object_meta_class_addr;
            if (object_class_isa & (1  *machOUnusedClass() {
    Dl_info info;
    dladdr((const void *)&machOUnusedClass, &info);

    const uint64_t mach_header = (uint64_t)info.dli_fbase;

    const struct section_64 *classlist = getsectbynamefromheader_64((const struct mach_header_64 *)mach_header, "__DATA", "__objc_classlist");
    const struct section_64 *selfrefs = getsectbynamefromheader_64((const struct mach_header_64 *)mach_header, "__DATA", "__objc_classrefs");

    if (classlist && selfrefs) {
        NSMutableSet *classlistSet = [[NSMutableSet alloc] init];
        for (UInt64 addr = classlist->offset; addr offset + classlist->size; addr += sizeof(const char **)) {
            uint64_t baseArrr = mach_header + addr;
            Class cls = (__bridge Class)(*(void **)(baseArrr));
            NSString *clsString = [NSString stringWithFormat:@"%@",cls];
            [classlistSet addObject:clsString];
        }

        NSMutableSet *selfrefsSet = [[NSMutableSet alloc] init];
        for (UInt64 addr = selfrefs->offset; addr offset + selfrefs->size; addr += sizeof(const char **)) {
            uint64_t baseArrr = mach_header + addr;

            Class cls = (__bridge Class)(*(void **)(baseArrr));
            while (cls) {
                [selfrefsSet addObject:[NSString stringWithFormat:@"%@",cls]];
                cls = [cls superclass];
            }
        }
        [classlistSet minusSet:selfrefsSet];

        return  classlistSet;
    }

    return nil;
}
缺点
1、runtimeUnusedClass 方法只有在第一次调用有效，而且必须手动浏览所有的页面（可以考虑放到线上收集）2、两种方法都是不准确的，但未使用的类一定在其中，最终需要手动再次确认。

iOS/自定义对象去重.html.body:
自定义对象去重
基本类型数据去重（4种方法）
1、利用 NSDictionary key 的唯一性2、利用 NSSet 的特性，数据不能重复3、利用 NSArray 的 containsObject 方法4、利用 NSArray 的 valueForKeyPath 方法，传入的 keyPath 为 @distinctUnionOfObjects.self
示例
NSArray *dataArray = @[@"a",@"b",@"c",@"d",@"e",@"f",@"g",
                       @"b",@"d",@"a"];

NSMutableDictionary *dict = [NSMutableDictionary dictionary];
for(NSString *str in dataArray) {
    [dict setValue:str forKey:str];
}
NSLog(@"dict = %@",[dict allKeys]);

NSSet *set = [NSSet setWithArray:dataArray];
NSLog(@"set = %@",[set allObjects]);

NSMutableArray *array = [NSMutableArray array];
for (NSString *str in dataArray) {
    if (![array containsObject:str]) {
        [array addObject:str];
    }
}
NSLog(@"array = %@",array);

NSArray *keyPathArray = [dataArray valueForKeyPath:@"@distinctUnionOfObjects.self"];
NSLog(@"keyPathArray = %@",keyPathArray);

通过上面4中方法均可过滤掉重复数据。
