数据读取流程
- (void)_readFrameNew;
清空上一帧上一帧数据，随后调用 _readFrameContinue
- (void)_readFrameContinue;

添加一个消费者

//添加消费者，用一个指定的长度，是否读到当前帧
- (void)_addConsumerWithDataLength:(size_t)dataLength callback:(data_callback)callback readToCurrentFrame:(BOOL)readToCurrentFrame unmaskBytes:(BOOL)unmaskBytes {
    //添加到消费者队列
    [_consumers addObject:[_consumerPool consumerWithScanner:nil handler:callback bytesNeeded:dataLength readToCurrentFrame:readToCurrentFrame unmaskBytes:unmaskBytes]];
    //扫描消费者所需要的字节数
    [self _pumpScanner];
}
- (void)_readFrameContinue {
        // 添加一个消费者
    [self _addConsumerWithDataLength:2 callback:^(SRWebSocket *self, NSData *data) {
        __block frame_header header = {0};
        const uint8_t *headerBuffer = data.bytes;
        uint8_t receivedOpcode = (SROpCodeMask & headerBuffer[0]);
        BOOL isControlFrame = (receivedOpcode == SROpCodePing || receivedOpcode == SROpCodePong || receivedOpcode == SROpCodeConnectionClose);

        header.opcode = receivedOpcode == 0 ? self->_currentFrameOpcode : receivedOpcode;
        header.fin = !!(SRFinMask & headerBuffer[0]);
        header.masked = !!(SRMaskMask & headerBuffer[1]);
        header.payload_length = SRPayloadLenMask & headerBuffer[1];

        headerBuffer = NULL;
        size_t extra_bytes_needed = header.masked ? sizeof(_currentReadMaskKey) : 0;
        if (header.payload_length == 126) {
            extra_bytes_needed += sizeof(uint16_t);
        } else if (header.payload_length == 127) {
            extra_bytes_needed += sizeof(uint64_t);
        }
        if (extra_bytes_needed == 0) {
                // 不需要读取扩展字段，则开始读取数据段
            [self _handleFrameHeader:header curData:self->_currentFrameData];
        } else {
                // 读取扩展字段
            [self _addConsumerWithDataLength:extra_bytes_needed callback:^(SRWebSocket *self, NSData *data) {
                size_t mapped_size = data.length;
                const void *mapped_buffer = data.bytes;
                size_t offset = 0;
                if (header.payload_length == 126) {
                    uint16_t newLen = EndianU16_BtoN(*(uint16_t *)(mapped_buffer));
                    header.payload_length = newLen;
                    offset += sizeof(uint16_t);
                } else if (header.payload_length == 127) {
                    header.payload_length = EndianU64_BtoN(*(uint64_t *)(mapped_buffer));
                    offset += sizeof(uint64_t);
                }

                if (header.masked) {
                    memcpy(self->_currentReadMaskKey, ((uint8_t *)mapped_buffer) + offset, sizeof(self->_currentReadMaskKey));
                }
                // 读取到扩展字段后，再读取 Data 数据
                [self _handleFrameHeader:header curData:self->_currentFrameData];
            } readToCurrentFrame:NO unmaskBytes:NO];
        }
    } readToCurrentFrame:NO unmaskBytes:NO];
}
读取帧的数据段
- (void)_handleFrameHeader:(frame_header)frame_header curData:(NSData *)curData {    
    BOOL isControlFrame = (frame_header.opcode == SROpCodePing || frame_header.opcode == SROpCodePong || frame_header.opcode == SROpCodeConnectionClose);

    if (!isControlFrame) {
        _currentFrameOpcode = frame_header.opcode;
        _currentFrameCount += 1;
    }
    //如果数据长度为0
    if (frame_header.payload_length == 0) {
        if (isControlFrame) {
                // 数据读完，开始处理数据
            [self _handleFrameWithData:curData opCode:frame_header.opcode];
        } else {
            if (frame_header.fin) {
                    // 数据读完，开始处理数据
                [self _handleFrameWithData:_currentFrameData opCode:frame_header.opcode];
            } else {
                // TODO add assert that opcode is not a control;
                // 数据还没读完，据需读取数据
                [self _readFrameContinue];
            }
        }
    } else {
            // 读取数据，回调后的处理流程同上
        [self _addConsumerWithDataLength:(size_t)frame_header.payload_length callback:^(SRWebSocket *self, NSData *newData) {
            if (isControlFrame) {
                [self _handleFrameWithData:newData opCode:frame_header.opcode];
            } else {
                if (frame_header.fin) {
                    [self _handleFrameWithData:self->_currentFrameData opCode:frame_header.opcode];
                } else {
                    // TODO add assert that opcode is not a control;
                    [self _readFrameContinue];
                }
            }
        } readToCurrentFrame:!isControlFrame unmaskBytes:frame_header.masked];
    }
}
开始处理数据
- (void)_handleFrameWithData:(NSData *)frameData opCode:(NSInteger)opcode {                
    BOOL isControlFrame = (opcode == SROpCodePing || opcode == SROpCodePong || opcode == SROpCodeConnectionClose);
    if (!isControlFrame) {
            // 里面是异步读取数据帧，跟下面的区别就是，非数据帧，需要先清理数据才能继续读取
        [self _readFrameNew];
    } else {
        dispatch_async(_workQueue, ^{
                // 数据帧不需要清理数据，直接读取
            [self _readFrameContinue];
        });
    }

    //frameData will be copied before passing to handlers
    //otherwise there can be misbehaviours when value at the pointer is changed
    // 开始处理数据，到这里整个接收流程也就走完了
    switch (opcode) {
        case SROpCodeTextFrame: {
            if ([self.delegate respondsToSelector:@selector(webSocketShouldConvertTextFrameToString:)] && ![self.delegate webSocketShouldConvertTextFrameToString:self]) {
                [self _handleMessage:[frameData copy]];
            } else {
                NSString *str = [[NSString alloc] initWithData:frameData encoding:NSUTF8StringEncoding];
                if (str == nil && frameData) {
                    [self closeWithCode:SRStatusCodeInvalidUTF8 reason:@"Text frames must be valid UTF-8"];
                    dispatch_async(_workQueue, ^{
                        [self closeConnection];
                    });
                    return;
                }
                [self _handleMessage:str];
            }
            break;
        }
        case SROpCodeBinaryFrame:
            [self _handleMessage:[frameData copy]];
            break;
        case SROpCodeConnectionClose:
            [self handleCloseWithData:[frameData copy]];
            break;
        case SROpCodePing:
            [self handlePing:[frameData copy]];
            break;
        case SROpCodePong:
            [self handlePong:[frameData copy]];
            break;
    }
}
发送数据流程


(void)send:(id)data;  



(void)_sendFrameWithOpcode:(SROpCode)opcode data:(id)data;  



(void)_writeData:(NSData *)data;



(void)_pumpWriting; // 开始写数据，数据量很大的话，单次写不完，会在流回调中继续写数据



- (void)_pumpWriting {
    NSUInteger dataLength = _outputBuffer.length;
    if (dataLength - _outputBufferOffset > 0 && _outputStream.hasSpaceAvailable) {
        // 这里写数据不一定全部写完
        NSInteger bytesWritten = [_outputStream write:_outputBuffer.bytes + _outputBufferOffset maxLength:dataLength - _outputBufferOffset];
        if (bytesWritten == -1) {
             return;
        }
        //表示已经写入的大小
        _outputBufferOffset += bytesWritten;
        //超过一定值，则重置
        if (_outputBufferOffset > 4096 && _outputBufferOffset > (_outputBuffer.length >> 1)) {
            //更新偏移量，重新生成新的buffer
            _outputBuffer = [[NSMutableData alloc] initWithBytes:(char *)_outputBuffer.bytes + _outputBufferOffset length:_outputBuffer.length - _outputBufferOffset];
            _outputBufferOffset = 0;
        }
    }
}
总结
两个线程，一个负责生产，一个负责消费，这种生产者-消费者模式运用的很巧妙。NSMutableData 运用的很好，避免了频繁生成 NSMutableData 对象；

